import React, { useEffect, useState } from 'react';
import { fetchEmployees, deactivateEmployee } from '../../services/api';
import { Link } from 'react-router-dom';
import './Employee.css';

function Employee() {
  const [employees, setEmployees] = useState([]); // State to hold employee data
  const [loading, setLoading] = useState(true); // Loading state
  const [recentlyDeleted, setRecentlyDeleted] = useState(null); // State to hold the recently deleted employee
  const [undoTimeout, setUndoTimeout] = useState(null); // Timeout for clearing the undo option

  // Fetch employees data from API
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const response = await fetchEmployees();
        setEmployees(response);
      } catch (error) {
        console.error('Error fetching employee data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);

  const handleDeactivate = async (employeeId) => {
    const confirm = window.confirm("Are you sure you want to delete this employee?");
    if (!confirm) return;

    const employeeToDelete = employees.find((employee) => employee.employeeId === employeeId);
    try {
      await deactivateEmployee(employeeId);
      setEmployees((prevEmployees) =>
        prevEmployees.filter((employee) => employee.employeeId !== employeeId)
      );

      setRecentlyDeleted(employeeToDelete);

      // Set a timeout to clear the undo option after 5 seconds
      const timeout = setTimeout(() => {
        setRecentlyDeleted(null);
      }, 5000);
      setUndoTimeout(timeout);
    } catch (error) {
      console.error('Error deleting the employee:', error);
    }
  };

  const handleUndo = () => {
    if (recentlyDeleted) {
      setEmployees((prevEmployees) => [recentlyDeleted, ...prevEmployees]);
      setRecentlyDeleted(null);

      // Clear the timeout when undo is clicked
      if (undoTimeout) clearTimeout(undoTimeout);
    }
  };

  return (
    <div>
      <header>
        <h2>Employee Details</h2>
      </header>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Department</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>Loading...</td>
            </tr>
          ) : employees.length > 0 ? (
            employees.map((employee) => (
              <tr key={employee.employeeId}>
                <td>{employee.employeeId}</td>
                <td>{employee.department.name}</td>
                <td>{employee.shortName}</td>
                <td>
                  {employee.status === 'ACTIVE' ? (
                    <button onClick={() => handleDeactivate(employee.employeeId)}>
                      Delete
                    </button>
                  ) : (
                    <span>Inactive</span>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>No Data</td>
            </tr>
          )}
        </tbody>
      </table>

      {recentlyDeleted && (
        <div className="undo-message">
          <p>Employee deleted.</p>
          <button onClick={handleUndo}>Undo</button>
        </div>
      )}

      <div className="buttons">
        <Link to="/add-new-employee">
          <button>Add New Employee</button>
        </Link>
        <button>Update Employee</button>
        <button>Shift Assign</button>
      </div>
    </div>
  );
}

export default Employee;
