import React, { useEffect, useState } from 'react';
import { fetchEmployees, deactivateEmployee } from '../../services/api'; // Import API services
import { Link } from 'react-router-dom';
import './Employee.css';

function Employee() {
  const [employees, setEmployees] = useState([]); // State to hold employee data
  const [loading, setLoading] = useState(true); // Loading state

  // Fetch employees data from API
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const response = await fetchEmployees();
        setEmployees(response);
      } catch (error) {
        console.error('Error fetching employee data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);

  
  const handleDeactivate = async (employeeId) => {
    // Ask for confirmation
    const confirm = window.confirm("Are you sure you want to deactivate this employee?");
    if (!confirm) return; // If user cancels, exit the function
  
    try {
      // Call API to deactivate employee
      await deactivateEmployee(employeeId);
  
      // After successful deactivation, update the employee status in the state
      setEmployees((prevEmployees) =>
        prevEmployees.map((employee) =>
          employee.employeeId === employeeId
            ? { ...employee, status: 'INACTIVE' } // Change status to INACTIVE in state
            : employee
        )
      );
    } catch (error) {
      console.error('Error deactivating the employee:', error);
    }
  };
  
  return (
    <div>
      <header>
        <h2>Employee Details</h2>
      </header>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Department</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>Loading...</td>
            </tr>
          ) : employees.length > 0 ? (
            employees.map((employee) => (
              <tr key={employee.employeeId}>
                <td>{employee.employeeId}</td>
                <td>{employee.department.name}</td>
                <td>{employee.shortName}</td>
                <td>
                  {employee.status === 'ACTIVE' ? (
                    <button onClick={() => handleDeactivate(employee.employeeId)}>
                      Delete
                    </button>
                  ) : (
                    <span>Inactive</span>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>No Data</td>
            </tr>
          )}
        </tbody>
      </table>
      <div className="buttons">
        <Link to="/add-new-employee">
          <button>Add New Employee</button>
        </Link>
        <button>Update Employee</button>
        <button>Shift Assign</button>
      </div>
    </div>
  );
}

export default Employee;
