import React, { useState } from 'react';
import { addEmployee } from '../../../services/api'; // Adjust the import path as necessary

function AddNewEmployee() {
    const [shortName, setShortName] = useState('');
    const [fullName, setFullName] = useState('');
    const [department, setDepartment] = useState(null); // Initialize as null
    const [designation, setDesignation] = useState('');
    const [nicNo, setNicNo] = useState('');
    const [departments] = useState([
        { departmentId: 1, name: 'Kitchen'},
        { departmentId: 2, name: 'Steward'},
        { departmentId: 3, name: 'Back Office'},
        { departmentId: 4, name: 'Front Office'},
        { departmentId: 5, name: 'Cleaning & Maintaintance'}
  ]);

  connt [employeeType] = useState([
        {name: 'Permanent'},
        {name : 'Temporary'}
  ]);
  const [selectedType, setSelectedType] = useState('');

  const handleChange = (event) => {
    setSelectedType(event.target.value);
  };

  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const employeeData = {
      shortName,
      fullName,
      department,
      designation,
      nicNo,
      employeeType,
      status: "ACTIVE"
    };

    console.log('Employee Data:', employeeData); // Log employee data

    try {
      const responseData = await addEmployee(employeeData);
      console.log('Employee added:', responseData);
      setSuccess('Employee added successfully!');
      setError(null);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
      setSuccess(null);
    }

    // Clear the form
    setShortName('');
    setFullName('');
    setDepartment(null); // Reset department selection
    setDesignation('');
    setNicNo('');
  };

  return (
    <div className="form-container">
    <form onSubmit={handleSubmit}>
      <div className='add-new-employee'>
        <label>
          <span>Employee Short Name:</span>
          <input
            type="text"
            value={shortName}
            onChange={(e) => setShortName(e.target.value)}
            required
          />
        </label>
      </div>

      <div className='add-new-employee'>
        <label>
          <span>Full Name:</span>
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
          />
        </label>
      </div>
      
      <div className='add-new-employee'>
        <label>
          <span>Department:</span>
          <select
            value={department ? department.departmentId : ''} // Use department ID for select value
            onChange={(e) => {
              const selectedDept = departments.find(dept => dept.departmentId === Number(e.target.value));
              setDepartment(selectedDept || null); // Set the selected department object
            }}
            required
          >
            <option value="">Select a department</option>
            {departments.map((dept) => (
              <option key={dept.departmentId} value={dept.departmentId}>
                {dept.name}
              </option>
            ))}
          </select>
        </label>
        </div>

        <div className='add-new-employee'>
        <label>
          <span>Designation:</span>
          <input
            type="text"
            value={designation}
            onChange={(e) => setDesignation(e.target.value)}
            required
          />
        </label>
        </div>

        <div className='add-new-employee'>
        <label>
          <span>NIC No:</span>
          <input
            type="text"
            value={nicNo}
            onChange={(e) => setNicNo(e.target.value)}
            required
          />
        </label>
        </div>
        <div>
          <label htmlFor="employeeType">Select Employee Type:</label>
          <select id="employeeType" value={selectedType} onChange={handleChange}>
            <option value="" disabled>Select type</option>
            {employeeType.map((type, index) => (
              <option key={index} value={type.name}>
                {type.name}
              </option>
            ))}
          </select>

          <p>Selected Employee Type: {selectedType}</p>
        </div>
            

      <button type="submit" className="add-button">Add</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </form>
    </div>
  );
};

export default AddNewEmployee;
