import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Select,
  MenuItem,
  Typography,
  Button,
} from '@mui/material';
import dayjs from 'dayjs';
import { fetchAttendance, saveUpdatedStatus } from '../../services/api'; // Make sure saveUpdatedStatus is defined in your API service.

function AttendanceTable(props) {
  const [daysInMonth, setDaysInMonth] = useState([]);
  const [modifiedIndices, setModifiedIndices] = useState(new Set());

  // Get all days of the current month
  useEffect(() => {
    const currentMonth = dayjs(`01 ${props.selectedMonth} 2000`, "DD MMMM YYYY").month();
    const currentYear = props.selectedYear;
    const days = [];
    const daysInCurrentMonth = dayjs().daysInMonth();

    // Create an array of days for the current month
    for (let i = 1; i <= daysInCurrentMonth; i++) {
      const date = dayjs(new Date(currentYear, currentMonth, i));
      const dayOfWeek = date.day();
      days.push({
        date: date.format('YYYY-MM-DD'),
        day: date.format('dddd'),
        dayOfWeek,
        timeIn: '',
        timeOut: '',
        attendanceStatus: 'ab',
        workHours: '',
        leaveType: '',
        updateAttendanceStatus: null,
      });
    }

    setDaysInMonth(days);

    // Load employee attendance
    const loadEmployeeAttendance = async () => {
      try {
        const employeeAttendanceList = await fetchAttendance(props.employeeId);
        
        const attendanceMap = {};
        employeeAttendanceList.forEach(record => {
          attendanceMap[record.date] = record;
        });

        const updatedDays = days.map(day => {
          const attendanceRecord = attendanceMap[day.date];
          if (attendanceRecord) {
            return {
              ...day,
              timeIn: attendanceRecord.actualStartTime || '',
              timeOut: attendanceRecord.actualEndTime || '',
              attendanceStatus: attendanceRecord.attendance || '',
              workHours: attendanceRecord.workHours || '',
              leaveType: attendanceRecord.leaveType || '',
              updateAttendanceStatus: attendanceRecord.updateAttendanceStatus || null,
            };
          }
          return day;
        });

        setDaysInMonth(updatedDays);
      } catch (error) {
        console.error("Error fetching employee attendance:", error);
      }
    };

    loadEmployeeAttendance();
  }, [props.employeeId, props.selectedMonth, props.selectedYear]);

  // Handle change for attendance fields
  const handleFieldChange = (index, field, value) => {
    const updatedDays = [...daysInMonth];
    updatedDays[index][field] = value;
    setDaysInMonth(updatedDays);

    // Mark the row as modified if attendanceStatus is changed
    if (field === 'attendanceStatus' && value !== daysInMonth[index].attendanceStatus) {
      setModifiedIndices(new Set(modifiedIndices).add(index));
    }
  };

  // Handle saving the updated attendance status
  const handleSaveUpdate = async (index) => {
    const updatedStatus = daysInMonth[index].attendanceStatus;
    try {
      await saveUpdatedStatus(props.employeeId, daysInMonth[index].date, updatedStatus);
      setModifiedIndices(prev => {
        const newModifiedIndices = new Set(prev);
        newModifiedIndices.delete(index);
        return newModifiedIndices;
      });
    } catch (error) {
      console.error("Error updating attendance status:", error);
    }
  };

  return (
    <TableContainer component={Paper} style={{ margin: 'auto' }}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Date</TableCell>
            <TableCell>Time In</TableCell>
            <TableCell>Time Out</TableCell>
            <TableCell>Attendance Status</TableCell>
            <TableCell>Work Hours</TableCell>
            <TableCell>Leave Type</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {daysInMonth.map((day, index) => (
            <TableRow
              key={index}
              style={{
                backgroundColor: day.dayOfWeek === 0 || day.dayOfWeek === 6 ? '#f5f5f5' : 'inherit',
              }}
            >
              <TableCell>
                <Typography>{day.date} ({day.day})</Typography>
              </TableCell>
              <TableCell>
                <TextField
                  type="time"
                  value={dayjs(day.timeIn).format("HH:mm:ss")}
                  onChange={(e) => handleFieldChange(index, 'timeIn', e.target.value)}
                  size="small"
                />
              </TableCell>
              <TableCell>
                <TextField
                  type="time"
                  value={dayjs(day.timeOut).format("HH:mm:ss")}
                  onChange={(e) => handleFieldChange(index, 'timeOut', e.target.value)}
                  size="small"
                />
              </TableCell>
              <TableCell>
                <Select
                  value={day.attendanceStatus}
                  onChange={(e) => handleFieldChange(index, "attendanceStatus", e.target.value)}
                  size="small"
                  displayEmpty
                >
                  <MenuItem value="ab">ab</MenuItem>
                  <MenuItem value="0.5">0.5</MenuItem>
                  <MenuItem value="1">1</MenuItem>
                </Select>
              </TableCell>
              <TableCell>
                <TextField
                  value={day.workHours}
                  size="small"
                />
              </TableCell>
              <TableCell>
                <Select
                  value={day.leaveType}
                  onChange={(e) => handleFieldChange(index, 'leaveType', e.target.value)}
                  displayEmpty
                  size="small"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  <MenuItem value="full day leave">Full Day Leave</MenuItem>
                  <MenuItem value="half day leave">Half Day Leave</MenuItem>
                  <MenuItem value="no pay leave">No Pay Leave</MenuItem>
                  <MenuItem value="holiday">Holiday</MenuItem>
                </Select>
              </TableCell>
              <TableCell>
                {modifiedIndices.has(index) && (
                  <Button
                    variant="contained"
                    color="primary"
                    size="small"
                    onClick={() => handleSaveUpdate(index)}
                  >
                    Save Update
                  </Button>
                )}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default AttendanceTable;
