import React, { useState, useEffect } from 'react';
import { addEmployee, getLastEmployeeId } from '../../../services/api';

function AddNewEmployee() {
    const [employeeId, setEmployeeId] = useState(null);
    const [shortName, setShortName] = useState('');
    const [fullName, setFullName] = useState('');
    const [department, setDepartment] = useState(null);
    const [designation, setDesignation] = useState('');
    const [nicNo, setNicNo] = useState('');
    const [employeeType, setEmployeeType] = useState('');
    const [epfNo, setEpfNo] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const departments = [
        { departmentId: 1, name: 'Kitchen' },
        { departmentId: 2, name: 'Steward' },
        { departmentId: 3, name: 'Back Office' },
        { departmentId: 4, name: 'Front Office' },
        { departmentId: 5, name: 'Cleaning & Maintenance' }
    ];

    // Fetch the last employee ID on component mount
    useEffect(() => {
        const fetchLastEmployeeId = async () => {
            try {
                const lastId = await getLastEmployeeId();
                setEmployeeId(lastId + 1); // Increment by 1 for new employee ID
            } catch (error) {
                console.error("Error fetching last employee ID:", error);
                setError("Could not retrieve last employee ID");
            }
        };

        fetchLastEmployeeId();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const employeeData = {
            employeeId,
            shortName,
            fullName,
            department,
            designation,
            nicNo,
            employeeType,
            epfNo: employeeType === 'PERMANENT' ? epfNo : '', // Include EPF No if Permanent
            status: "ACTIVE"
        };

        try {
            const responseData = await addEmployee(employeeData);
            setSuccess('Employee added successfully!');
            setError(null);
        } catch (error) {
            setError(error.message);
            setSuccess(null);
        }

        // Clear the form
        setShortName('');
        setFullName('');
        setDepartment(null);
        setDesignation('');
        setNicNo('');
        setEmployeeType('');
        setEpfNo('');
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <div className='add-new-employee'>
                    <label>
                        <span>Employee ID:</span>
                        <input type="text" value={employeeId || ''} readOnly />
                    </label>
                </div>

                {/* Other input fields remain unchanged */}

                <button type="submit" className="add-button">Add</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>{success}</p>}
            </form>
        </div>
    );
};

export default AddNewEmployee;
