import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // For getting the employeeId from the URL
import { getEmployeeById, updateEmployee } from '../../../services/api'; // Adjust the API methods

function UpdateEmployee() {
    const { employeeId } = useParams(); // Get the employeeId from the URL
    const [employee, setEmployee] = useState({
        employeeId: '',
        shortName: '',
        fullName: '',
        department: null,
        designation: '',
        nicNo: '',
        employeeType: '',
        epfNo: ''
    });

    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const departments = [
        { departmentId: 1, name: 'Kitchen' },
        { departmentId: 2, name: 'Steward' },
        { departmentId: 3, name: 'Back Office' },
        { departmentId: 4, name: 'Front Office' },
        { departmentId: 5, name: 'Cleaning & Maintenance' }
    ];

    useEffect(() => {
        const fetchEmployeeDetails = async () => {
            try {
                // Fetch the employee data by ID
                const response = await getEmployeeById(employeeId);
                setEmployee(response); // Assuming response has the employee data
            } catch (error) {
                console.error('Error fetching employee data:', error);
                setError('Failed to load employee data');
            }
        };

        fetchEmployeeDetails();
    }, [employeeId]); // Fetch data only when employeeId changes

    const handleSubmit = async (e) => {
        e.preventDefault();

        const updatedEmployeeData = {
            ...employee,
            epfNo: employee.employeeType === 'PERMANENT' ? employee.epfNo : '', // Include EPF No if Permanent
            status: "ACTIVE"
        };

        try {
            await updateEmployee(updatedEmployeeData); // Call API to update employee
            setSuccess('Employee updated successfully!');
            setError(null);
        } catch (error) {
            console.error('Error updating employee:', error);
            setError(error.message);
            setSuccess(null);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee((prevState) => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <div className='add-new-employee'>
                    <label>
                        <span>Employee ID:</span>
                        <input
                            type="text"
                            name="employeeId"
                            value={employee.employeeId}
                            onChange={handleChange}
                            readOnly
                        />
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>Employee Short Name:</span>
                        <input
                            type="text"
                            name="shortName"
                            value={employee.shortName}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>Full Name:</span>
                        <input
                            type="text"
                            name="fullName"
                            value={employee.fullName}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>Department:</span>
                        <select
                            name="department"
                            value={employee.department ? employee.department.departmentId : ''}
                            onChange={(e) => {
                                const selectedDept = departments.find(dept => dept.departmentId === Number(e.target.value));
                                setEmployee({ ...employee, department: selectedDept || null });
                            }}
                            required
                        >
                            <option value="">Select a department</option>
                            {departments.map((dept) => (
                                <option key={dept.departmentId} value={dept.departmentId}>
                                    {dept.name}
                                </option>
                            ))}
                        </select>
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>Designation:</span>
                        <input
                            type="text"
                            name="designation"
                            value={employee.designation}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>NIC No:</span>
                        <input
                            type="text"
                            name="nicNo"
                            value={employee.nicNo}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>

                <div className='add-new-employee'>
                    <label>
                        <span>Employee Type:</span>
                        <select
                            name="employeeType"
                            value={employee.employeeType}
                            onChange={handleChange}
                            required
                        >
                            <option value="">Select employee type</option>
                            <option value="PERMANENT">Permanent</option>
                            <option value="TEMPORARY">Temporary</option>
                        </select>
                    </label>
                </div>

                {employee.employeeType === 'PERMANENT' && (
                    <div className='add-new-employee'>
                        <label>
                            <span>EPF No:</span>
                            <input
                                type="text"
                                name="epfNo"
                                value={employee.epfNo}
                                onChange={handleChange}
                                required
                            />
                        </label>
                    </div>
                )}

                <button type="submit" className="add-button">Update</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>{success}</p>}
            </form>
        </div>
    );
}

export default UpdateEmployee;
